"use strict";
// Objects in TypeScript
// Just like in JavaScript, but you can define the shape (types) of the object.
let person2 = {
    name: "John",
    age: 30
};
// Optional properties are allowed
let anotherPerson = {
    name: "Alice"
};
// You can now create objects that match the Labtop interface
let legion = {
    manufactor: 'lenovo',
    model: 'legion',
    year: 2022,
};
// You can now create objects that match the Person interface
let user = {
    name: "Alice",
    age: 25
};
let myCar = {
    brand: "Toyota",
    model: "Corolla"
};
let myBook = {
    title: "1984",
    author: "George Orwell"
};
const greet = (name) => {
    return `Hello, ${name}`;
};
let myDog = {
    name: "Buddy",
    breed: "Golden Retriever"
};
let point = { x: 10, y: 20 };
